import { useState, useEffect } from "react";
import { getEpisodes } from "../lib/httpRequests";

import Episode from "./Episode";
import LoadingSpinner from "./UI/LoadingSpinner";

import classes from "./EpisodesList.module.css";

const EpisodesList = () => {
  const [episodes, setEpisodes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    getEpisodes().then((dataArr) => {
      // setEpisodes(dataArr[0]);
      // setIsLoading(false);
    });

    console.log("jkdjbsdfvbj");
  }, []);
  console.log(episodes);
  return (
    <>
      {isLoading && <LoadingSpinner />}
      {!isLoading && (
        <>
          <ul className="episodes-list">
            {episodes.map((episode) => (
              <Episode
                key={episode.id}
                episode={episode.episode}
                name={episode.name}
                air_date={episode.air_date}
                charactersNo={episode.characters.length}
              />
            ))}
          </ul>

          {/* <div className={classes.pagination}></div> */}
        </>
      )}

      {/* ------------------------------- for Mobile  ------------
      <button class="mobile-episodes">
        <h3>View Episodes</h3>
      </button> */}
    </>
  );
};

export default EpisodesList;
