import { useState } from "react";
import classes from "./Episode.module.css";

const Episode = (props) => {
  const [activeEpisode, setActiveEpisode] = useState();
  const [active, setActive] = useState(false);

  const getCharactersHandler = (event) => {
    if (activeEpisode !== props.episode) {
      console.log("if")
    }
    
    
    setActiveEpisode(props.episode);
    

    console.log(props.episode);
  };


  return (
    <li
      className={`${classes.episode} ${
        active ? `${classes["active-episode"]}` : ""
      }`}
      episode={props.episode}
    >
      <div className={classes.control}>
        <label>Episode name:</label>
        <output>{props.name}</output>
      </div>
      <div className={classes.control}>
        <label>Air date:</label>
        <output>{props.air_date}</output>
      </div>
      <div className={classes.control}>
        <label>Episode:</label>
        <output>{props.episode}</output>
      </div>
      <div className={classes.control}>
        <label>Characters:</label>
        <button
          type="button"
          disabled={active}
          className={` ${active ? `${classes["button-active"]}` : ""}`}
          episode={props.episode}
          onClick={getCharactersHandler}
        >
          View <span>{props.charactersNo}</span> characters
        </button>
      </div>
    </li>
  );
};

export default Episode;
