import { useState } from "react";

import Header from "./components/Header";
import Container from "./components/UI/Container";
import EpisodesList from "./components/Episodes/EpisodesList";
import CharactersList from "./components/Characters/CharactersList";

import classes from "./App.module.css";

function App() {
  const [activeEpisode, setActiveEpisode] = useState();
  const [expandClass, setExpandClass] = useState("");  
  const [expand, setExpand] = useState(false);

  const setActiveEpisodeHandler = (episode) => {
    setActiveEpisode(episode);
  };

  const toggleExpand = () => {
    setExpand((prevState) => {
      if (!prevState) {
        setExpandClass("expand");
      } else {
        setExpandClass("");
      }
      return !prevState;
    });

  };

  return (
    <>
      <Header />
      <Container className={`${classes["container-left"]} ${expandClass}`}>
        <EpisodesList
          activeEpisode={activeEpisode}
          setActiveEpisodeHandler={setActiveEpisodeHandler}
          toggleExpand={toggleExpand}
        />
      </Container>
      <Container className={classes["container-rigth"]}>
        <CharactersList activeEpisode={activeEpisode} />
      </Container>
    </>
  );
}

export default App;
