  const [episodes, setEpisodes] = useState()
  const [isLoading, setIsLoading] = useState(true)
  return (
    <ul className="episodes-list">
      <LoadingSpinner />

      {/* <Episode /> */}

      <div className={classes.pagination}></div>

      {/* ------------------------------- for Mobile  ------------
      <button class="mobile-episodes">
        <h3>View Episodes</h3>
      </button> */}
    </ul>
  );
};

export default EpisodeList;
