import { useEffect, useState } from "react";

import classes from "./Character.module.css";

import { getCharacter } from "../lib/httpRequests";

const Character = (props) => {
  const [character, setCharacter] = useState({});
  const [clase, setClase] = useState(classes.character);

  const refreshCharacters = (link) => {
    getCharacter(link).then((data) => {
      data.time = new Date().getTime();
      setCharacter(data);
      setClase(classes.character + classes.animation);
    });
  };

  useEffect(() => {
    refreshCharacters(props.characterLink);
    console.log(character.time);
  }, []);


  return (
    <li
      // className={{classes.character, classes.animation}}
      className={clase}
      id={`character_${character.id}`}
    >
      <div className={classes["character-img"]}>
        <img src={character.image} alt={character.time} />
      </div>
      <div className={classes["character-content"]}>
        <div className={classes.section}>
          <h4>{character.name}</h4>
          <output className={classes.status}>
            <span className={classes["status-icon"]}></span>
            {character.status} - {character.species}
          </output>
        </div>
        <div className={classes.section}>
          <label>Gender:</label>
          <output>{character.gender}</output>
        </div>
        <div className={classes.section}>
          <label>Last known location:</label>
          <output>{character.location?.name}</output>
        </div>
      </div>
    </li>
  );
};

export default Character;
