import { useState } from "react";
import classes from "./Episode.module.css";

const Episode = (props) => {
  const [activeEpisode, setActiveEpisode] = useState(false);

  const getCharactersHandler = () => {
    setActiveEpisode(true);
    console.log("get chracters handler");
  };

  console.log(activeEpisode);

  return (
    <li
      className={`${classes.episode} ${activeEpisode ? "active-episode": ""}`}
      episode={props.episode}
    >
      <div className={classes.control}>
        <label>Episode name:</label>
        <output>{props.name}</output>
      </div>
      <div className={classes.control}>
        <label>Air date:</label>
        <output>{props.air_date}</output>
      </div>
      <div className={classes.control}>
        <label>Episode:</label>
        <output>{props.episode}</output>
      </div>
      <div className={classes.control}>
        <label>Characters:</label>
        <button
          type="button"
          episode={props.episode}
          onClick={getCharactersHandler}
          // disabled={activeEpisode}
        >
          View <span>{props.charactersNo}</span> characters
        </button>
      </div>
    </li>
  );
};

export default Episode;
