import { useCallback } from "react";

const link = "https://rickandmortyapi.com/api/";

// requestConfig = {
//   link,
//   errorMessage,
//   urlParams: [{property: "episode", value: "", required: true/false}]
// };

const useHttp = (applyData) => {
  const prepareLink = (newLink) => {
    return newLink.replace(link, "");
  };

  const processURLParams = (urlParams) => {
    let queryArr = [];
    if (!urlParams) {
      return "";
    }
    for (let param of urlParams) {
      if (param.required && !param.value) {
        throw new Error(`Value not defined for ${param.property}`);
      }
      queryArr.push(`${param.property}=${param.value}`);
    }
    return "?" + queryArr.join("&");
  };

  const sendRequest = useCallback(
    (requestConfig) => {
      const url = link + prepareLink(requestConfig.link) + processURLParams(requestConfig.urlParams)
console.log(url)
      fetch(url, {
        method: "GET",
      })
        .then((response) => {
          if (!response.ok) {
            return Promise.reject(new Error(requestConfig.errorMessage));
          }
          return response.json();
        })
        .then((data) => {
          applyData(data);
        })
        .catch((error) => {
          console.log(error.message);
        });
    },
    [applyData]
  );

  return {
    sendRequest,
  };
};

export default useHttp;
