import { useState } from "react";
import classes from "./MobileEpisodesList.module.css";

const MobileEpisodesList = () => {
  const [expand, setExpand] = useState(false)

  const toggleExpandHandler = () => {
    setExpand(!expand);
    
  };

  return (
    <button className={classes["mobile-episodes"]} onClick={toggleExpandHandler}>
      <h3>{expand ? "View Characters" : "View Episodes"}</h3>
    </button>
  );
};

export default MobileEpisodesList;
