import { useCallback } from "react";

const link = "https://rickandmortyapi.com/api/";

// requestConfig = {
//   link,
//   errorMessage
// };

const useHttp = (applyData) => {
  const prepareLink = (newLink) => {
    return newLink.replace(link, "");
  };

  const sendRequest = useCallback(
    (requestConfig) => {
      fetch(link + prepareLink(requestConfig.link), {
        method: "GET",
      })
        .then((response) => {
          if (!response.ok) {
            return Promise.reject(new Error(requestConfig.errorMessage));
          }
          return response.json();
        })
        .then((data) => {
          applyData(data);
        })
        .catch((error) => {
          console.log(error.message);
        });
    },
    [applyData]
  );

  return {
    sendRequest,
  };
};

export default useHttp;
