import { useState, useEffect } from "react";
import { getEpisodes } from "../lib/httpRequests";

import Episode from "./Episode";
import LoadingSpinner from "./UI/LoadingSpinner";
import Pagination from "./Pagination";

import classes from "./EpisodesList.module.css";

const EpisodesList = () => {
  const [episodes, setEpisodes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalTodos, setTotalTodos] = useState();
  const [isLoading, setIsLoading] = useState(false);

  const refreshEpisodes = (pageNo) => {
    setIsLoading(true);
    getEpisodes(pageNo).then((dataArr) => {
      setEpisodes(dataArr[0]);
      setIsLoading(false);
    });
  };

  useEffect(() => {
    refreshEpisodes(currentPage);
  }, []);

  console.log(episodes);

  return (
    <>
      {isLoading && <LoadingSpinner />}
      {!isLoading && (
        <>
          <ul className="episodes-list">
            {episodes.map((episode) => (
              <Episode
                key={episode.id}
                episode={episode.episode}
                name={episode.name}
                air_date={episode.air_date}
                charactersNo={episode.characters.length}
              />
            ))}
          </ul>
          <Pagination />
        </>
      )}

      {/* ------------------------------- for Mobile  ------------
      <button class="mobile-episodes">
        <h3>View Episodes</h3>
      </button> */}
    </>
  );
};

export default EpisodesList;
