import { useState, useEffect, useCallback } from "react";

import Episode from "../Episodes/Episode";
import LoadingSpinner from "../UI/LoadingSpinner";
import Pagination from "../Pagination";
import MobileEpisodesList from "./MobileEpisodesList";

import useHttp from "../../hooks/use-http";

const EpisodesList = (props) => {
  const [episodes, setEpisodes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState();
  const [isLoading, setIsLoading] = useState(false);

  const setEpisodesData = useCallback((dataArr) => {
    setEpisodes(dataArr[0]);
    setTotalPages(dataArr[1]);    
    setIsLoading(false);
  }, []);

  const { sendRequest: getEpisodes } = useHttp(setEpisodesData);

  // const refreshEpisodes = (pageNo) => {
  //   setIsLoading(true);
  //   getEpisodes(pageNo)
  //     .then((dataArr) => {
  //       setEpisodes(dataArr[0]);
  //       setTotalPages(dataArr[1]);
  //       setIsLoading(false);
  //     })
  //     .catch((error) => {
  //       console.log(error.message);
  //     });
  // };

  const switchPage = (pageNo) => {
    setCurrentPage(pageNo);
  };

  useEffect(() => {
    setIsLoading(true);
    getEpisodes({
      link: "episode?page=" + currentPage,
      errorMessage: "Could not get Episodes!",
    });
    // console.log(isLoading);
  }, [currentPage, getEpisodes]);
  
console.log(isLoading);
  return (
    <>
      {isLoading && <LoadingSpinner />}
      {!isLoading && (
        <>
          <ul className="episodes-list">
            {episodes.map((episode) => (
              <Episode
                key={episode.id}
                episode={episode.episode}
                name={episode.name}
                air_date={episode.air_date}
                charactersNo={episode.characters.length}
                setActiveEpisodeHandler={props.setActiveEpisodeHandler}
                active={props.activeEpisode === episode.episode}
              />
            ))}
          </ul>
          <Pagination
            totalPages={totalPages}
            currentPage={currentPage}
            switchPage={switchPage}
          />
        </>
      )}

      {/* ------------- for Mobile  ------------ */}
      <MobileEpisodesList />
    </>
  );
};

export default EpisodesList;
