import { useCallback } from "react";

const link = "https://rickandmortyapi.com/api/";

// requestConfig = {
//   link,
//   errorMessage,
//   urlParams: [{property: "episode", value: "", required: true/false}]
// };

const useHttp = (applyData) => {

  const prepareLink = (newLink) => {
    return newLink.replace(link, "");
  };

  const processURLParams = (urlParams) => {
    let queryArr = [];
    for (let param in urlParams) {
      if (param.required && !param.value) {
        throw new Error(`Value not defined for ${param.property}`);
      }
      queryArr.push(param.property + param.value);
    }
  };

  const sendRequest = useCallback((requestConfig) => {
    fetch(link + prepareLink(requestConfig.link), {
      method: "GET",
    })
      .then((response) => {
        if (!response.ok) {
          return Promise.reject(new Error(requestConfig.errorMessage));
        }
        return response.json();
      })
      .then((data) => {
        applyData(data);
      })
      .catch((error) => {
        console.log(error.message);
      });
  }, []);

  return {
    sendRequest,
  };
};

export default useHttp;
